import{_ as e,c as s,o as a,d as r}from"./app.bc8efb32.js";var t="/assets/users.684522f6.png";const f='{"title":"Users administration","description":"","frontmatter":{},"headers":[{"level":2,"title":"Users administration","slug":"users-administration"},{"level":3,"title":"Business rules","slug":"business-rules"},{"level":3,"title":"Global Rules","slug":"global-rules"},{"level":3,"title":"Pages rules","slug":"pages-rules"},{"level":3,"title":"Schema","slug":"schema"},{"level":3,"title":"Rest api Examples","slug":"rest-api-examples"}],"relativePath":"software/users/users.md","lastUpdated":1641577789378}',i={},n=r('<h2 id="users-administration" tabindex="-1">Users administration <a class="header-anchor" href="#users-administration" aria-hidden="true">#</a></h2><h3 id="business-rules" tabindex="-1">Business rules <a class="header-anchor" href="#business-rules" aria-hidden="true">#</a></h3><table><thead><tr><th>Role</th><th>Description</th></tr></thead><tbody><tr><td>superadmin</td><td>Can create, modify any user of any stores</td></tr><tr><td>admin</td><td>Can create, modify any user its pertaining store</td></tr><tr><td>admin_store</td><td>Can create, modify any user its pertaining store</td></tr><tr><td>admin_retail</td><td>Can only modify their profile, Deletion is not supported</td></tr></tbody></table><h3 id="global-rules" tabindex="-1">Global Rules <a class="header-anchor" href="#global-rules" aria-hidden="true">#</a></h3><ul><li>User even if superuser can\u2019t change roles</li><li>Superuser can\u2019t create superuser</li><li>Admins can\u2019t delete or modify superuser</li></ul><h3 id="pages-rules" tabindex="-1">Pages rules <a class="header-anchor" href="#pages-rules" aria-hidden="true">#</a></h3><h4 id="user-list" tabindex="-1">User List <a class="header-anchor" href="#user-list" aria-hidden="true">#</a></h4><ul><li>When login in as super admin all users are displayed in the list</li><li>When login in a any other admin only users from same store are displayed in the list</li><li>User cannot delete himself, he has to rely on other admin</li></ul><h4 id="create-modify-user-page" tabindex="-1">Create / modify user page <a class="header-anchor" href="#create-modify-user-page" aria-hidden="true">#</a></h4><ul><li>Cannot assign group to superadmin</li><li>Self cannot edit it\u2019s own groups</li><li>Self cannot change merchant store</li><li>Default selected merchant is current user merchant</li></ul><h3 id="schema" tabindex="-1">Schema <a class="header-anchor" href="#schema" aria-hidden="true">#</a></h3><p><strong>USER</strong><strong>MERCHANT_STORE</strong><strong>SM_GROUP</strong><strong>USER_GROUP</strong><strong>PERMISSION_GROUP</strong><strong>PERMISSION</strong></p><p>Users belong to MERCHANT_STORE. Users are assigned to groups having predefined sets of permissions.</p><p><img src="'+t+'" alt="User database tables" title="User database tables"></p><h3 id="rest-api-examples" tabindex="-1">Rest api Examples <a class="header-anchor" href="#rest-api-examples" aria-hidden="true">#</a></h3><p>Some postman examples are available here (import collection in Postman)</p><p><a href="https://github.com/shopizer-ecommerce/postman/tree/master/services/users" target="_blank" rel="noopener noreferrer">Users management</a></p>',17),l=[n];function o(d,h,u,p,c,m){return a(),s("div",null,l)}var _=e(i,[["render",o]]);export{f as __pageData,_ as default};
